/* View: ROLESTOMENU */
CREATE VIEW ROLESTOMENU(
    ROLEID,
    ROLENAME,
    LONGROLENAME,
    DCL_USER_NAME,
    DCL_LONG_USER_NAME,
    MENUITEMID,
    DCLNAME)
AS
select r.rolesid roleid, r.rolename, r.longrolename, u.dcl_user_name, u.dcl_long_user_name, rm.MENUITEMID, d.dclname from DCL_RolesMenu rm, DCL_Roles r, dcl_users u, DCL_SCRIPTS d
where r.rolesid=rm.roleid and rm.MENUITEMID=d.numseq and u.dcl_role=r.rolesid;


