CREATE OR ALTER PROCEDURE ADD_EMPL (
    FULL_NAME VARCHAR(45),
    CLASS SMALLINT,
    DEP_CODE INTEGER,
    TARIF_SUM NUMERIC(9,2),
    TYPE_CODE SMALLINT)
AS
DECLARE VARIABLE TMP1 INTEGER;
begin
select count(*) from empl e where upper(e.full_name)=upper(:full_name) and e.class=:class and e.subdcode=:dep_code into :tmp1;
if (:tmp1=0) then
Begin
    if (:tarif_sum=0) then tarif_sum=null;
    Insert into empl(Full_Name, SubDCode, Class, Salary, EmplType) values(:full_name, :dep_code, :class, :tarif_sum, :type_code);
End
else
    exception alrady_exists;
end^


CREATE OR ALTER PROCEDURE ADD_MENU_ITEM_TO_ROLE (
    ROLEID INTEGER,
    MENUITEMID INTEGER)
RETURNS (
    ISAPPEND INTEGER)
AS
declare variable TMP1 integer;
begin
  select count(R.ROLEID)
  from DCL_ROLESMENU R
  where R.ROLEID = :ROLEID and
        R.MENUITEMID = :MENUITEMID
  into TMP1;
  if (:TMP1 = 0) then
  begin
    insert into DCL_ROLESMENU (ROLEID, MENUITEMID)
    values (:ROLEID, :MENUITEMID);
    ISAPPEND = 1;
    suspend;
  end
  else
  begin
    ISAPPEND = 0;
    suspend;
  end
end^


