CREATE OR ALTER PROCEDURE DAY_OF_WEEK (
    ADATE DATE)
RETURNS (
    RESULT INTEGER)
AS
DECLARE VARIABLE ELAPSED INTEGER;
BEGIN
  Elapsed=ADate-Cast('1.1.1996' as Date);
  if (Elapsed = 0) THEN
  Result = 0;
  ELSE
  Result = Elapsed-(CAST((Elapsed / 7)-0.5 AS INTEGER)*7);
suspend;
END^


