    TARIFGROUP SMALLINT,
    TARIF_BEG DATE,
    TARIF_END DATE,
    GRUP SMALLINT,
    M_NAME VARCHAR(50),
    GOS_NOM VARCHAR(50),
    DRIVER_ID INTEGER,
    DELFLGM SMALLINT,
    HTARIF INTEGER,
    CAR_ID INTEGER)
AS
declare variable tmp1 integer;
begin
if (:actualy_date is null) then actualy_date='now';
  for select CAR_ID, M_NAME, GOS_NOM, DRIVER_ID, DELFLGM, HTARIF, TARIF_ID, TARIF_NAME, TREF_SUM, TARIFGROUP, TARIF_BEG, TARIF_END, GRUP from mashins m, tarifs t where m.htarif=t.tarif_id into :CAR_ID, :M_NAME, :GOS_NOM, :DRIVER_ID, :DELFLGM, :HTARIF, :TARIF_ID, :TARIF_NAME, :TREF_SUM, :TARIFGROUP, :TARIF_BEG, :TARIF_END, :GRUP do
  begin
  tmp1=0;
    for select tarif_id, tarif_name, tref_sum, tarifgroup, tarif_beg, tarif_end, grup from tarifs t where t.tarifgroup=:tarifgroup and t.grup=:grup into :tarif_id, :tarif_name, :tref_sum, :tarifgroup, :tarif_beg, :tarif_end, :grup do
      begin
        if (tarif_beg<=:actualy_date and tarif_end>=:actualy_date) then
        begin
            select t.tarif_id, t.tarif_name, t.tref_sum, t.tarifgroup, t.tarif_beg, t.tarif_end, t.grup from tarifs t where t.tarif_beg>=:actualy_date and t.tarif_end<=:actualy_date and t.tarifgroup=:tarifgroup and t.grup=:grup into :tarif_id, :tarif_name, :tref_sum, :tarifgroup, :tarif_beg, :tarif_end, :grup;
            suspend;
            tmp1=1;
        end
      end
        if (tmp1=0) then
        begin
            select CAR_ID, M_NAME, GOS_NOM, DRIVER_ID, DELFLGM, HTARIF, TARIF_ID, TARIF_NAME, TREF_SUM, TARIFGROUP, TARIF_BEG, TARIF_END, GRUP from mashins m, tarifs t where m.htarif=t.tarif_id and m.car_id=:car_id and t.tarif_id=:tarif_id into :CAR_ID, :M_NAME, :GOS_NOM, :DRIVER_ID, :DELFLGM, :HTARIF, :TARIF_ID, :TARIF_NAME, :TREF_SUM, :TARIFGROUP, :TARIF_BEG, :TARIF_END, :GRUP;
            suspend;
        end
  end
end^


CREATE OR ALTER PROCEDURE UNCORRECTLY_TARIFS
RETURNS (
    TARIF_ID INTEGER,
    TARIF_NAME VARCHAR(50),
    TARIF_SUM NUMERIC(9,2),
    TARIFGROUP INTEGER,
    TARIF_BEG DATE,
    TARIF_END DATE,
    GRUP INTEGER,
    KONFLICT VARCHAR(50),
    TARIF_BEG_DATE DATE,
    TARIF_END_DATE DATE)
AS
declare variable tarif_grup integer;
declare variable tar_t integer;
declare variable tarif_id_tmp integer;
begin
for select distinct t.tarifgroup from tarifs t into :tar_t do
begin
    for select distinct t.grup from tarifs t where t.tarifgroup=:tar_t into :tarif_grup do
    begin
        for select t.tarif_id, t.tarif_beg, t.tarif_end from tarifs t where t.grup=:tarif_grup and t.tarifgroup=:tar_t into :tarif_id_tmp, :tarif_beg_date, :tarif_end_date do
        begin
            for select 'Вхождения в дату начала' KONFLICT, t.tarif_id, t.tarif_name, t.tref_sum, t.tarifgroup, t.tarif_beg, t.tarif_end, t.grup from tarifs t where t.grup=:tarif_grup and t.tarifgroup=:tar_t and t.tarif_beg<=:tarif_beg_date and t.tarif_end>=:tarif_beg_date into KONFLICT, tarif_id, tarif_name, tarif_sum, tarifgroup, tarif_beg, tarif_end, grup do
            begin
                if (:tarif_id<>:tarif_id_tmp) then suspend;
            end
            for select 'Вхождения в дату конца' KONFLICT, t.tarif_id, t.tarif_name, t.tref_sum, t.tarifgroup, t.tarif_beg, t.tarif_end, t.grup from tarifs t where t.grup=:tarif_grup and t.tarifgroup=:tar_t and t.tarif_beg<=:tarif_end_date and t.tarif_end>=:tarif_end_date into KONFLICT, tarif_id, tarif_name, tarif_sum, tarifgroup, tarif_beg, tarif_end, grup do
            begin
                if (:tarif_id<>:tarif_id_tmp) then suspend;
            end
        end
    end
end
end^


CREATE OR ALTER PROCEDURE WEEK_NUMBER (
    ADATE DATE)
RETURNS (
    RESULT INTEGER)
AS
DECLARE VARIABLE W INTEGER; /* week number */
DECLARE VARIABLE Y INTEGER; /* year the week belongs to */
BEGIN
  Result = ((EXTRACT(YEARDAY FROM ADate) - EXTRACT(WEEKDAY FROM ADate-1) + 7) / 7e0)+1;
  SUSPEND;
END^


CREATE OR ALTER PROCEDURE WORKLIST_CONSOLIDATE (
    BEG_DATE DATE,
    END_DATE DATE)
RETURNS (
    WL_ID INTEGER,
    WORK_DATE DATE,
    DRIVER_ID INTEGER,
    MASHINE_ID INTEGER,
    TARIF_ID INTEGER,
    ADD_NAME VARCHAR(45),
    TARIF_RATE NUMERIC(9,2),
    MAIN_REC INTEGER,
    MAIN_NUM INTEGER,
    EMP_ID INTEGER,
    FULL_NAME VARCHAR(150),
    CLASS SMALLINT,
    CLASS_RATE SMALLINT,
    CLASS_SUM NUMERIC(9,2),
    SALARY NUMERIC(9,2),
    M_NAME VARCHAR(150),
    GOS_NOM VARCHAR(15),
    HOUR_TARIF_RATE NUMERIC(9,2),
    OBJ_ID INTEGER,
    O_NAME VARCHAR(150),
    TARIF_SUM NUMERIC(9,2),
    AD_RATE NUMERIC(9,2),
    CALC_TYPE VARCHAR(150),
    STENDBY_TIME NUMERIC(9,2),
    ORDER_F INTEGER,
    EMPL_TYPE INTEGER,
    EMPL_T_NAME VARCHAR(45),
    REC_TYPE SMALLINT,
    ADD2ALL_HOURS SMALLINT,
    ADD2ALL SMALLINT,
    DEPEND_ADD INTEGER,
    DEP_CODE INTEGER)
AS
declare variable TMP1 numeric(9,2);
declare variable TMP_HTR numeric(9,2);
declare variable MAINSUM numeric(9,2);
declare variable HTR numeric(9,2);
declare variable DRIVER_ID_NAME integer;
declare variable TOTALL_SUM numeric(9,2);
declare variable TOTALL_SUM2 numeric(9,2);
declare variable TMP_O_NAME varchar(150);
declare variable TMP_O_ID integer;
declare variable NO_CLASS smallint;
declare variable ALL_HOURS numeric(9,2);
declare variable ADD2FRV smallint;
declare variable MASTER smallint;
declare variable SLAVE smallint;
declare variable MASTER_SUM numeric(9,2);
declare variable TMP_MAINSUM numeric(9,2);
declare variable TMP2_HTR numeric(9,2);
declare variable TMP_SBT numeric(9,2);
declare variable ADDON_SUM numeric(9,2);
declare variable WORKTIME numeric(9,2);
declare variable ADDONSUM2 numeric(9,2);
declare variable ALL_HOURS2 numeric(9,2);
declare variable TARIFA_GRUPA integer;
declare variable TARIF_GROUP integer;
declare variable HTR_ID integer;
begin
  if (:BEG_DATE is null) then
    BEG_DATE = 'NOW';
  if (:END_DATE is null) then
    END_DATE = 'NOW';
  ORDER_F = 0;
  for select distinct WL.DRIVER_ID
      from WORKS_LIST WL, EMPL E
      where WL.DRIVER_ID = E.EMP_ID and
            WL.WORK_DATE between :BEG_DATE and :END_DATE
      order by E.FULL_NAME
      into :DRIVER_ID_NAME
  do
  begin
    TOTALL_SUM = 0;
    ALL_HOURS = 0;
    TOTALL_SUM2 = 0;
    ADDONSUM2 = 0;
    ALL_HOURS2 = 0;
    for select M.HTARIF, EMT.NO_CLASS, WL.WL_ID, WL.WORK_DATE, WL.DRIVER_ID, WL.MASHINE_ID, WL.TARIF_ID, WL.TARIF_RATE,
               AD.RATE, WL.MAIN_REC, WL.MAIN_NUM, E.EMP_ID, E.FULL_NAME, E.CLASS, E.SALARY, M.M_NAME, M.GOS_NOM,
               T.TREF_SUM, O.OBJ_ID, O.O_NAME, E.EMPLTYPE, EMT.TYPE_NAME, E.SUBDCODE
        from WORKS_LIST WL, MASHINS M, EMPL E, ADDONS_REF AD, OBJECTS O, TARIFS T, EMPL_TYPES EMT
        where WL.DRIVER_ID = :DRIVER_ID_NAME and
              E.EMPLTYPE = EMT.TYPE_ID and
              M.HTARIF = T.TARIF_ID and
              WL.MASHINE_ID = M.CAR_ID and
              WL.DRIVER_ID = E.EMP_ID and
              WL.TARIF_ID = AD.AD_ID and
              WL.OBJECT_ID = O.OBJ_ID and
              WL.WORK_DATE between :BEG_DATE and :END_DATE and
              WL.MAIN_REC is not null
        order by E.FULL_NAME, WL.WORK_DATE
        into :HTR_ID, :NO_CLASS, :WL_ID, :WORK_DATE, :DRIVER_ID, :MASHINE_ID, :TARIF_ID, :TARIF_RATE, :AD_RATE,
             :MAIN_REC, :MAIN_NUM, :EMP_ID, :FULL_NAME, :CLASS, :SALARY, :M_NAME, :GOS_NOM, :HOUR_TARIF_RATE, :OBJ_ID,
             :O_NAME, :EMPL_TYPE, :EMPL_T_NAME, :DEP_CODE
    do
    begin
      REC_TYPE = 1;
      ALL_HOURS = 0;
      ORDER_F = ORDER_F + 1;
      /*htr=:hour_tarif_rate;*/
      if (:SALARY is not null) then
      begin
        if (:SALARY <> 0) then
        begin
          TARIF_SUM = :SALARY;
          TMP1 = :TARIF_SUM;
          MAINSUM = :SALARY;
          ADD_NAME = 'Фикс. оклад';
          CALC_TYPE = 'Фикс. оклад';
          HTR = :SALARY;
        end
      end
      else
      begin
        if (:NO_CLASS = 1) then
          CLASS = null;
        select T.TARIFGROUP, T.GRUP
        from TARIFS T
        where T.TARIF_ID = :HTR_ID
        into :TARIFA_GRUPA, :TARIF_GROUP;
        select T.TREF_SUM
        from TARIFS T
        where T.TARIFGROUP = :TARIFA_GRUPA and
              T.GRUP = :TARIF_GROUP and
              T.TARIF_BEG <= :WORK_DATE and
              T.TARIF_END >= :WORK_DATE
        into :HOUR_TARIF_RATE;

        TARIF_SUM = :HOUR_TARIF_RATE * :TARIF_RATE;
        CLASS_RATE = cast(case :CLASS
                            when 1 then 25
                            when 2 then 10
                            else 0
                          end as smallint);
        CLASS_SUM = (:HOUR_TARIF_RATE * :TARIF_RATE * cast(case :CLASS
                                                             when 1 then 0.25
                                                             when 2 then 0.10
                                                             else 0
                                                           end as numeric(9,2)));
        MAINSUM = :TARIF_SUM;
        TMP1 = :TARIF_SUM + CLASS_SUM;
        ADD_NAME = 'ФРВ';
        CALC_TYPE = 'ФРВ';
        STENDBY_TIME = TARIF_RATE;
        TARIF_RATE = null;
      end
      ALL_HOURS = ALL_HOURS + STENDBY_TIME;
      suspend;
      ADDON_SUM = 0;
      WORKTIME = STENDBY_TIME;
      for select M.HTARIF, AD.MASTER, AD.SLAVE, AD.ADD2FRV, WL.WL_ID, WL.WORK_DATE, WL.DRIVER_ID, WL.MASHINE_ID,
                 WL.TARIF_ID, AD.ADD_NAME, WL.TARIF_RATE, AD.RATE, WL.MAIN_REC, WL.MAIN_NUM, E.EMP_ID, E.FULL_NAME,
                 M.M_NAME, M.GOS_NOM, T.TREF_SUM, O.OBJ_ID, O.O_NAME, WL.STENDBY_TIME, E.EMPLTYPE, EMT.TYPE_NAME,
                 EMT.ADD2ALL_HOURS, AD.ADD2ALL, E.SUBDCODE
          from WORKS_LIST WL, MASHINS M, EMPL E, ADDONS_REF AD, OBJECTS O, TARIFS T, EMPL_TYPES EMT
          where WL.DRIVER_ID = :DRIVER_ID_NAME and
                E.EMPLTYPE = EMT.TYPE_ID and
                M.HTARIF = T.TARIF_ID and
                WL.MASHINE_ID = M.CAR_ID and
                WL.DRIVER_ID = E.EMP_ID and
                WL.TARIF_ID = AD.AD_ID and
                WL.OBJECT_ID = O.OBJ_ID and
                WL.WORK_DATE between :BEG_DATE and :END_DATE and
                WL.MAIN_NUM = :MAIN_REC and
                WL.DRIVER_ID = :DRIVER_ID and
                WL.WORK_DATE = :WORK_DATE and
                WL.MASHINE_ID = :MASHINE_ID
          order by E.FULL_NAME, WL.WORK_DATE, AD.MASTER, AD.SLAVE
          into :HTR_ID, :MASTER, :SLAVE, :ADD2FRV, :WL_ID, :WORK_DATE, :DRIVER_ID, :MASHINE_ID, :TARIF_ID, :ADD_NAME,
               :TARIF_RATE, :AD_RATE, :MAIN_REC, :MAIN_NUM, :EMP_ID, :FULL_NAME, :M_NAME, :GOS_NOM, :HOUR_TARIF_RATE,
               :OBJ_ID, :O_NAME, :STENDBY_TIME, :EMPL_TYPE, :EMPL_T_NAME, :ADD2ALL_HOURS, :ADD2ALL, :DEP_CODE
      do
      begin
        select T.TARIFGROUP, T.GRUP
        from TARIFS T
        where T.TARIF_ID = :HTR_ID
        into :TARIFA_GRUPA, :TARIF_GROUP;
        select T.TREF_SUM
        from TARIFS T
        where T.TARIFGROUP = :TARIFA_GRUPA and
              T.GRUP = :TARIF_GROUP and
              T.TARIF_BEG <= :WORK_DATE and
              T.TARIF_END >= :WORK_DATE
        into :HOUR_TARIF_RATE;

        ORDER_F = ORDER_F + 1;
        HTR = :HOUR_TARIF_RATE;
        CALC_TYPE = 'Надбавка:' || :ADD_NAME;
        TMP_HTR = HOUR_TARIF_RATE;
        HOUR_TARIF_RATE = null;
        if (:AD_RATE = 0) then
          AD_RATE = :TARIF_RATE;
        else
          TARIF_RATE = :AD_RATE;

        REC_TYPE = 2;

        if (:ADD2ALL_HOURS = 1) then
        begin
          /*РњРµС…Р°РЅРёР·Р°С‚РѕСЂС‹*/
          if (:TARIF_SUM = 0) then
          begin
            MAINSUM = (TMP_HTR * STENDBY_TIME) + CLASS_SUM;
          end

          if (:STENDBY_TIME is null) then
            ADD2FRV = 1;
          if (:STENDBY_TIME = 0) then
            ADD2FRV = 1;

          if (:ADD2FRV = 1) then
          begin
            ALL_HOURS = ALL_HOURS + STENDBY_TIME;
            HOUR_TARIF_RATE = (TMP_HTR * AD_RATE) / 100;
          end

          TARIF_SUM = (:HTR * :AD_RATE * :STENDBY_TIME) / 100;
        end
        else
        begin
          /*Водители*/

          /*    if (:slave=1) then
    Begin
    tmp_mainsum=mainsum;
    tmp_sbt=stendby_time;
    stendby_time=master_sum;
    end*/
          WORKTIME = 0;
          if (ADD2FRV = 1) then
          begin
            ALL_HOURS = ALL_HOURS + STENDBY_TIME;
            TARIF_SUM = (:HTR * :AD_RATE * (:STENDBY_TIME + WORKTIME)) / 100;
            HOUR_TARIF_RATE = (TMP_HTR * AD_RATE) / 100;
          end
          else
          begin
            WORKTIME = 0;
            TARIF_SUM = (:HTR * :AD_RATE * :STENDBY_TIME) / 100;
          end

          /*    if (:master=1) then
    begin
        master_sum=tarif_sum;
        hour_tarif_rate=(tmp_htr*ad_rate)/100;
    end
    else
        hour_tarif_rate=null;
    if (slave=1) then
    begin
        mainsum=tmp_mainsum;
        HTR=tmp2_htr;
        stendby_time=tmp_sbt;
        hour_tarif_rate=null;
    end*/
          CLASS = null;
          CLASS_RATE = null;
          CLASS_SUM = null;
        end
        TMP1 = TMP1 + TARIF_SUM;
        CLASS = null;
        CLASS_RATE = null;
        CLASS_SUM = null;
        suspend;
        ADDON_SUM = ADDON_SUM + TARIF_SUM;
      end
      ADDONSUM2 = ADDONSUM2 + ADDON_SUM;
      TARIF_SUM = :TMP1;
      TOTALL_SUM = TOTALL_SUM + :TMP1;
      CALC_TYPE = 'Итог: ' || FULL_NAME;
      CLASS_RATE = null;
      CLASS_SUM = null;
      TARIF_ID = null;
      CLASS = null;
      CLASS_RATE = null;
      CLASS_SUM = null;
      MAIN_NUM = null;
      MAIN_REC = null;
      REC_TYPE = 9;
      TARIF_RATE = null;
      HOUR_TARIF_RATE = null;
      ADD_NAME = null;
      AD_RATE = null;
      WL_ID = null;
      STENDBY_TIME = ALL_HOURS;
      ALL_HOURS2 = ALL_HOURS2 + ALL_HOURS;
      ORDER_F = ORDER_F + 1;
      suspend;
    end
    ORDER_F = ORDER_F + 1;
    WORK_DATE = null;
    TARIF_SUM = ADDONSUM2;
    CALC_TYPE = 'Сумма надбавок: ' || FULL_NAME;
    OBJ_ID = null;
    O_NAME = null;
    GOS_NOM = null;
    M_NAME = null;
    MASHINE_ID = null;
    CLASS_RATE = null;
    CLASS_SUM = null;
    TARIF_ID = null;
    CLASS = null;
    CLASS_RATE = null;
    CLASS_SUM = null;
    MAIN_NUM = null;
    MAIN_REC = null;
    REC_TYPE = 11;
    TARIF_RATE = null;
    HOUR_TARIF_RATE = null;
    ADD_NAME = null;
    AD_RATE = null;
    WL_ID = null;
    STENDBY_TIME = null;
    suspend;

    REC_TYPE = 12;
    WORK_DATE = null;
    CALC_TYPE = 'Итог Общий : ' || :FULL_NAME;
    TARIF_ID = null;
    TARIF_RATE = null;
    M_NAME = null;
    MASHINE_ID = null;
    O_NAME = TMP_O_NAME;
    OBJ_ID = TMP_O_ID;
    GOS_NOM = null;
    HOUR_TARIF_RATE = null;
    ADD_NAME = null;
    AD_RATE = null;
    MAIN_REC = null;
    MAIN_NUM = null;
    WL_ID = null;
    STENDBY_TIME = ALL_HOURS2;
    TARIF_SUM = :TOTALL_SUM;
    ORDER_F = ORDER_F + 1;
    suspend;
  end
end^


